import { Subject } from "rxjs";
import { Ingredient } from "../shared/ingredient.model";
export class ShoppingListService {
  constructor() {
    this.ingredientChanged = new Subject();
    this.startedEditing = new Subject();
    this.ingredients = [new Ingredient('Chicken Breast ', 10), new Ingredient('Mushrooms ', 6)];
  }

  getIngredients() {
    return this.ingredients.slice();
  }

  addIngredient(ingedient) {
    this.ingredients.push(ingedient);
    this.ingredientChanged.next(this.ingredients.slice());
  }

  addIngrdients(ingredients) {
    this.ingredients.push(...ingredients);
    this.ingredientChanged.next(this.ingredients.slice());
  }

  updateIngredients(index, newIngredient) {
    this.ingredients[index] = newIngredient;
    this.ingredientChanged.next(this.ingredients.slice());
  }

  deleteIngredient(index) {
    this.ingredients.splice(index);
    this.ingredientChanged.next(this.ingredients.slice());
  }

  getIngredient(index) {
    return this.ingredients[index];
  }

}
